import React from 'react';
// import logo from './logo.svg';
// import './App.css';
import { ChatMessage, ChatState } from '../types';
import { ChatContext } from '../ChatContext';
import * as PIXI from 'pixi.js';
import myImage from '../assets/cards.png';
import { identity } from 'rxjs';

class Poker extends React.Component {
  static contextType = ChatContext;
  public pixi_cnt:any = null;
  public canvasApp:any = null;
  public avatar: any = null;
  public state: any = {app: null}
  constructor(props:any) {
    super(props);
    this.pixi_cnt = null;


  }

  componentDidMount () {

    //initiate socket connection
    this.context.init();

    const observable = this.context.onMessage();

    observable.subscribe((m: ChatMessage) => {
      let messages = this.state.messages;

      messages.push(m);
      this.setState({ messages: messages });
    });

    this.canvasApp = new PIXI.Application({width: 1000, height: 1000, transparent:false, backgroundColor: 0x1099bb})

    this.setState({app:this.canvasApp});

    const canvas = document.getElementById('canvas');


    if(canvas !== null) 
    canvas.appendChild(this.canvasApp.view);

    // create a new Sprite from an image path
    // const bunny = new PIXI.Text('Basic text in pixi');

      // const bunny = PIXI.Sprite.from('https://cdn.dribbble.com/users/375867/screenshots/2790167/bunny-rabbit-cute-game-character-sprite-sheets_dribbble.jpg');
      const bunny = PIXI.Sprite.from('bunny.png');

      // center the sprite's anchor point
      if(bunny !== null)
      console.log('BBBBBBB');

      bunny.anchor.set(0.5);

      // move the sprite to the center of the screen
      bunny.x = 100;
      bunny.y = 100;

      this.canvasApp.stage.addChild(bunny);

      // Listen for animate update
      this.canvasApp.ticker.add((delta:any) => {
          // just for fun, let's rotate mr rabbit a little
          // delta is 1 if running at 100% performance
          // creates frame-independent transformation
          bunny.rotation += 0.1 * delta;
          //console.log('lll');

      });


  }

  componentWillUnmount () {
    this.context.disconnect();
  }

  updatePixiCnt= (element:any) => {
    // the element is the DOM object that we will use as container to add pixi stage(canvas)

    //now we are adding the application to the DOM element which we got from the Ref.
    if(this.pixi_cnt === null && this.canvasApp !== null) {
      this.pixi_cnt = element;
       this.pixi_cnt.appendChild(this.canvasApp.view);
       //The setup function is a custom function that we created to add the sprites. We will this below
       this.setup();
    }

    console.log('kkk');

 };

