  PIXI.Loader.shared.add("avatar",myImage)
       .load(this.initialize);
};
initialize = () => {
   //We will create a sprite and then add it to stage and (0,0) position
   this.avatar = new PIXI.Sprite(PIXI.Loader.shared.resources["avatar"].texture);
   this.state.app.stage.addChild(this.avatar);

};

render() {

  requestAnimationFrame(this.render);
  this.canvasApp.renderer.render(this.canvasApp.stage);
    return <div id='canvas'/>;
  }
}

export default Poker;
