import * as PIXI from 'pixi.js';
import {CardSheet} from '../commons/Resource';

class Card
{
    private avatar: any = null;
    private value: string = '';
    constructor(_value: string)
    {
       this.setValue(_value);
       this.Init();
    }

    public  Init()
    {
        let cardNumber = parseInt(this.value[2]);
        let cardType = parseInt(this.value[3]);
        let cardIndex = cardType*cardNumber;
        this.avatar = PIXI.Sprite.from(CardSheet[cardIndex]);
        this.avatar.anchor.set(0.5);
        this.avatar.scale.x = 0.3;
        this.avatar.scale.y = 0.3;
    }

    public getAvatar()
    {
        return this.avatar;
    }

    public setAvatar(_avatar:any)
    {
        this.avatar = _avatar;
    }

    public getValue()
    {
        return this.value;
    }

    public setValue(_value:string)
    {
        this.value = _value;
    }


}

export default Card;
