import React from 'react';
import * as PIXI from 'pixi.js';

abstract class Game extends React.Component {
  public canvasApp:any = null;
  constructor(props:any) {
    super(props);
  }

public componentDidMount () {

    this.InitPixiJS();
    this.Init();
    // Game loop
    this.canvasApp.ticker.add((delta:any) => {

        this.Update(delta);
    });

  }

 public InitPixiJS()
 {
    let width = document.body.clientWidth;
    let height = document.body.clientHeight;

    this.canvasApp = new PIXI.Application({width: 1600, height: 800, transparent:false, backgroundColor: 0x1099bb})

    this.setState({app:this.canvasApp});

    const canvas = document.getElementById('canvas');
    if(canvas !== null) 
    canvas.appendChild(this.canvasApp.view);
 }

 abstract Init():void;
 abstract Update(delta:any):void;

 public componentWillUnmount () {
    this.context.disconnect();
  }



public render() {
    return <div id='canvas'/>;
  }
}

export default Game;
